import React from 'react';
import hospimain from './Images/hospimain.jpeg';
import Card from './Card'; // Assuming you have a Card component

const diseases = [
  { name: "Hair fall", url: hospimain },
  { name: "Skin Issues", url: hospimain },
  { name: "Bone Issues", url: hospimain },
  { name: "Digestive Issues", url: hospimain }
];

function Diseases() {
  // No need to use useState here unless you're managing state within this component

  return (
    <div>
      {diseases.map((item, index) => (
        <Card key={index}>
          <img src={item.url} alt={item.name} />
          <h2>{item.name}</h2>
        </Card>
      ))}
    </div>
  );
}

export default Diseases;
