import React from 'react'
import hospimain from './Images/hospimain.jpeg';
import harshit from './Images/harshit.jpg';
export default function Article() {

    const articleContent=
    [{imgSrc:harshit, name:"Dr. Harshit Shakya",title:"BHMS Banaras",description:"We have been able to identify the responsibility of various stakeholders. Steps taken include whitelisting of illegal loan apps. Through MeitY, Google has also tightened the uploading policy for its app store. It now requires that developers of personal loan apps targeting Indian users have to declare whether they are licensed by the RBI for lending or if they are not lenders themselves and they only facilitate lending by banks or NBFCs and comply with the laws including the Fair Practices Code of the RBI,” Joshi told The Indian Express"},
    {imgSrc:{hospimain}, name:"Dr. Akash Kushwah",title:"BHMS Banaras",description:"We have been able to identify the responsibility of various stakeholders. Steps taken include whitelisting of illegal loan apps. Through MeitY, Google has also tightened the uploading policy for its app store. It now requires that developers of personal loan apps targeting Indian users have to declare whether they are licensed by the RBI for lending or if they are not lenders themselves and they only facilitate lending by banks or NBFCs and comply with the laws including the Fair Practices Code of the RBI,” Joshi told The Indian Express"}];

    const renderCard=(content,index)=>
    {
        return(
        <div key={index} className="card article p-6 border-2 col-6">
            <img className="p-4" src={content.imgSrc} alt="doc pic" height="300px"/>
            <h3 className="p-2">{content.name}</h3>
            <p className="p-2">{content.title}</p>
            <p className="p-2">{content.description}</p>
        </div>);
    }

  return (
    <div>
        <h2 className="p-4 text-center">Our Doctors</h2>
       <div className="container flex justify-center row"> 
          {articleContent.map((content,index)=>(renderCard(content,index)))}
        </div>
    </div>
  );

  }
