import React from 'react'
import hospimain from './Images/hospimain.jpeg';

function Article() {

    const articleContent=[{imgSrc:{hospimain},name:"Dr. Harshit Shakya",title:"BHMS Banaras",description:"Hello I am a doctor ."},
    {imgSrc:{hospimain},name:"Dr. Akash Kushwah",title:"BHMS Banaras",description:"Hello I am a better doctor ."}];

    const renderCard=(content,index)=>
    {
        return(
        <div key={index} className="article p-6 border-2 col-6">
            <img src={content.imgSrc} alt="doc pic"/>
            <h3>{content.name}</h3>
            <p className="pb-2">{content.title}</p>
            <p className="pb-2">{content.description}</p>
        </div>);
    }

  return (
    <div>
       <div className="container row">
          {articleContent.map((content,index)=>(renderCard(content,index)))}
        </div>
    </div>
  )
}

export default Article
