import React from 'react'

function Article() {

    const articleContent=[{imgSrc:"hospimain.jpeg",name:"Dr. Harshit Shakya",title:"BHMS Banaras",description:"Hello I am a doctor ."}];

    const renderCard=(content,index)=>
    {
        return(
        <div key={index} className="p-6 border-2">
            <img src={content.imgSrc} alt="doc pic"/>
            <h3>{content.name}</h3>
            <p className="pb-2">{content.title}</p>
            <p className="pb-2">{content.description}</p>
        </div>);
    }

  return (
    <div>
       <div>
          {articleContent.map((content,index)=>(renderCard(content,index)))}
        </div>
    </div>
  )
}

export default Article
