import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars } from '@fortawesome/free-solid-svg-icons';

function MobileDropDown() {

  const [isOpen, setIsOpen] = useState(false);


  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };


  return (
    <div className="dropdown">

      <button onClick={toggleDropdown}><FontAwesomeIcon icon={faBars}/></button>

      {isOpen && (
        <div className="dropdownmenu border-2">
          <ul className="border-1" >
            <li onClick={toggleDropdown}><a className="custom-link" href="/">Home</a></li>
            <li onClick={toggleDropdown}><a className="custom-link" href="#doc">About Us</a></li>
            <li onClick={toggleDropdown}><a className="custom-link" href="#foot">Contact Us</a></li>
          </ul>
        </div>
      )}
    </div>
  );
}

export default MobileDropDown;
