import React from 'react';
import harshit from './Images/harshit.jpg';
import '../App.css';

function Diseases() {
    const diseases = [
        { name: "Hairfall", url: harshit },
        { name: "Skin Diseases", url: harshit },
        { name: "Digestive Issues", url: harshit },
        { name: "Cardiovascular Diseases", url: harshit }
    ];

    const renderDisease = (content, index) => {
        return (
            <div className='cardDisease p-2 col-3' key={index}>
                <img className="imgi" src={content.url} alt="first" width="200px" height="200px" style={{ borderRadius: '10px' }} />
                <h5 className='diseaseName'>{content.name}</h5>
            </div>
        );
    };

    return (
    <div className='headers'>
        Treament Available for Diseases
        <div className='diseaseContainer row oveflow-hidden'>
            {diseases.map((content, index) => renderDisease(content, index))}
        </div>
        </div>
    );
}

export default Diseases;
