import React from 'react';
import harshit from './Images/harshit.jpg';
import '../App.css';

function Diseases() {
    const diseases = [
        { name: "Hairfall", url: harshit },
        { name: "Skin Diseases", url: harshit },
        { name: "Digestive Issues", url: harshit },
        { name: "Cardiovascular Diseases", url: harshit },
        { name: "Hairfall", url: harshit },
        { name: "Skin Diseases", url: harshit },
        { name: "Digestive Issues", url: harshit },
        { name: "Cardiovascular Diseases", url: harshit }
    ];

    return (
        <div className='headersnew'>
            Treatment Available for Diseases
            <div className='marquee-container'>
                <div className='marquee'>
                    {diseases.map((content, index) => (
                        <div className='cardDisease col-xl-3 col-12 pr-3' key={index}>
                            <img className="imgi pt-2" src={content.url} alt="first" style={{ borderRadius: '20px' }} />
                            <h5 className='diseaseName'>{content.name}</h5>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default Diseases;
